import React, { useState } from 'react';
import axios from 'axios'
import './App.css'


const DeleteExpenseForm = ({show, list, submitHandler_}) => {
    
    const class_placerholder = show ? 'modal display-block' : 'modal display-none';

    return (
        <div className={class_placerholder}>
            <div className='modal-main'>
                <h2 className="black"> Expense: </h2>
                <form onSubmit={submitHandler_}>
                    <label for="cars">Choose a car:</label>
                        <select name="cars" id="cars">
                            <option value="volvo">Volvo</option>
                            {list.map( (element) => (<option value="expense">{element}</option>))}
                        </select>
                        
                        <button type="submit">Submit</button> 
                </form>
            </div>
        </div>
    )
}

export default DeleteExpenseForm
