import React, { useState } from 'react';
import axios from 'axios'
import './App.css'


const DeleteExpenseForm = ({show, submitHandler_}) => {
    
    const class_placerholder = show ? 'modal display-block' : 'modal display-none';
    
    const [tempList, setTempList] = useState([]);


    axios.get("http://localhost:8080/expense/allExpenses")
        .then(res => {
            setTempList({tempList: res})
        })
    return tempList.map((element) => {
        return (
            <div className={class_placerholder}>
                <div className='modal-main'>
                    <h2 className="black"> Expense: </h2>
                    <form onSubmit={submitHandler_}>
                        <label for="cars">Choose a car:</label>
                            <select name="cars" id="cars">
                                <option value="volvo">{tempList.length}</option>
                                {tempList.map( (element) => (<option value="expense">{element.expense}</option>))}
                            </select>
                            
                            <button type="submit">Submit</button> 
                    </form>
                </div>
            </div>
        )
    })
}

export default DeleteExpenseForm
