import React, { useState } from 'react';
import axios from 'axios'
import './App.css'

import Select from 'react-select'


const EditExpenseForm = ({myList, handleClose, show, submitHandler}) => {

    const class_placerholder = show ? 'modal display-block' : 'modal display-none';

    return (
          
        <div className={class_placerholder}>
                <div className='modal-main'>
                    <h2 className="black"> Expense: </h2>
                    <div>
                        
                        <Select 
                            options={myList}
                            getOptionLabel={(option) => option.expense}
                            getOptionValue={(option) => option.id} 
                        />
                        
                    </div>
                </div>
             
        </div>
        
    )
    
}

export default EditExpenseForm
