import React, { useState } from 'react';
import axios from 'axios'
import './App.css'
import Button from 'react-bootstrap/Button'
import { faWindowRestore } from '@fortawesome/free-solid-svg-icons';
//import 'bootstrap/dist/css/bootstrap.min.css'


export default class Register extends React.Component {
    constructor(props) {
        super(props);
        this.state = {

        }

        this.submitUser = this.submitUser.bind(this);
    }

    submitUser = e => {
        e.preventDefault();
        
        if (e.target[1].value === e.target[2].value) {
            axios.post("http://localhost:8080/user/addRow",{
                username: e.target[0].value,
                password: e.target[1].value
            }).then(response => {
                
                const res = response.data;
                console.log(res);
                
                // this.setState({
                //     expenses: [...this.state.expenses, newExpenseObject]
                // }, function() {
                //     let updatedMap = new Map(this.state.spentValsForAllExpenses);
                //     updatedMap.set(newExpenseObject.id, 0.0);
                //     this.setState({spentValsForAllExpenses: updatedMap});
                // })
            }).catch(error => {
                console.log(error)
            }) 
            document.getElementById('RegisterFormInput').value=''; 
            window.alert("User has been created!") 
        }
        else {
            e.target[1].value = '';
            window.alert("Passwords do not match.")
        }
    }

    render() {
        return (<>
            <div className='modal-main'>
                    <h1 className="mainTitle" id='formText'>Register</h1>
                    <form onSubmit={this.submitUser}>
                        <label className="black">User: 
                        <div>
                            <input id="registerFormInput" required type="text" name="expense"  placeholder="Username_99" />
                        </div>
                        </label>
                        <label className="black">Password:  
                            <input name="password" placeholder="Password123" required type="password"/>
                        </label>
                        <label className="black">Confirm Password:  
                            <input name="confirmPassword" placeholder="Password123" required type="password" />
                        </label>
                        <div className="buttons-flex">
                            <button type="submit" className="buttons-invariant">Submit</button>
                            <button type="button" className="buttons-invariant">Close</button>
                        </div>
                        
                    </form>
                    
                </div>
        </>)
    }
}
