import React from 'react';
import axios from 'axios'
import './App.css'
import AddExpensePopUp from './AddExpensePopUp';

export default class Home extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            expenses: [],
            addExpenseToggle: false
        };
    
        // This binding is necessary to make `this` work in the callback
        this.handleAddExpense= this.handleAddExpense.bind(this);
    }

    togglePop = () => {
        this.setState({
          addExpenseWindow: !this.state.addExpenseWindow
        });
      };


    renderTableData() {
        return this.state.expenses.map((element) => {
           const { id, expense, budget, spent } = element //destructuring
           return (
              <tr>
                 <td>{expense}</td>
                 <td>{budget}</td>
                 <td>{spent}</td>
              </tr>
           )
        })
     }

    componentDidMount() {
        axios.get("http://localhost:8080/expense/allExpenses")
        .then(res => {
            const expenses = res.data;
            this.setState({expenses});
        })
    }
    

    handleAddExpense() {
        const modal = document.querySelector(".modal")
        const closeBtn = document.querySelector(".close")
        modal.style.display = "block";
        closeBtn.addEventListener("click", () => {
          modal.style.display = "none";
        })
    }

    render() {
        return (
            <div>
                <h1 className="mainTitle">Budget Tracker</h1>
                <button onClick={this.togglePop}> click me </button>
                {this.state.addExpenseToggle ? <AddExpensePopUp toggle={this.togglePop} /> : null}
                <table className="expense-table">
                    <thead>
                        <tr>
                            <th>Expense</th>
                            <th>Budget</th>
                            <th>Spent</th>
                        </tr>
                    </thead>
                    <tbody>
                        {this.renderTableData()}
                    </tbody>
                </table>
            </div>
        )
    }
}
