import React, { useState } from 'react';
import axios from 'axios'
import './App.css'
import {Link} from "react-router-dom";


export default class Transactions extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            transactions: [],
            sortBySelection: -1
        };

        this.renderTableData = this.renderTableData.bind(this);
        this.handleSortByChange = this.handleSortByChange.bind(this);
    }

    handleSortByChange(e) {
        let selectedElement = -1;
        {this.state.transactions.map((element) => {
            if (element.id === e.target.value) {
                selectedElement = element.id;
            }
        })}
        this.setState({ sortBySelection: selectedElement });
    }

    renderTableData() {
        return this.state.transactions.reverse().map((element) => {
            
           return (
              <tr>
                 <td>{element.transactionDate}</td>
                 <td>{element.expenseValue}</td>
                 <td>{element.payee}</td>
                 <td>${(element.spent).toFixed(2)}</td>
              </tr>
           )
        })
        
    }

    componentDidMount() {
        axios.get("http://localhost:8080/transaction/allTransactions")
        .then(res => {
            const transactions = res.data;
            this.setState({transactions});
        })
    }

    render() {
        return (
            <div>
                <h1 className="mainTitle">All Transactions</h1>
                <div>
                    <Link to="/">
                        <button>Go Back</button>
                    </Link>
                </div>
                <div className="flex">
                    <label>Sort By:
                        <select onChange={this.handleSortByChange}>
                            {this.transactions.map((element) => {
                                <option value={element.expense}>{element.expense}</option>
                            })}
                        </select>
                    </label>
                </div>
                <table className="expense-table">
                    <thead>
                        <tr>
                            <th>Date</th>
                            <th>Category</th>
                            <th>Payee</th>
                            <th>Spent</th>
                        </tr>
                    </thead>
                    <tbody>
                        {this.renderTableData()}
                    </tbody>
                </table>
            </div>
        )
    }
}
