import React, { useState } from 'react';
import axios from 'axios'
import './App.css'

import Select from 'react-select'
import { colors } from 'react-select/dist/declarations/src/theme';


const EditExpenseForm = ({myList, handleClose, show, submitHandler}) => {

    const class_placerholder = show ? 'modal display-block' : 'modal display-none';
    const customStyles = {
        control: (base, state) => ({
          ...base,
          background: "#023950",
          // match with the menu
          borderRadius: state.isFocused ? "3px 3px 0 0" : 3,
          // Overwrittes the different states of border
          borderColor: state.isFocused ? "yellow" : "green",
          // Removes weird border around container
          boxShadow: state.isFocused ? null : null,
          "&:hover": {
            // Overwrittes the different states of border
            borderColor: state.isFocused ? "red" : "blue"
          }
        }),
        menu: base => ({
          ...base,
          // override border radius to match the box
          borderRadius: 0,
          // kill the gap
          //marginTop: 0
        }),
        menuList: base => ({
          ...base,
          // kill the white space on first and last option
          padding: 0,
          colors: black
        })
    };
    return (
          
        <div className={class_placerholder}>
                <div className='modal-main'>
                    <h2 className="black"> Expense: </h2>
                    <div>
                        
                        <Select 
                            options={myList}
                            styles={customStyles}
                            getOptionLabel={(option) => option.expense}
                            getOptionValue={(option) => option.id} 
                        />
                        
                    </div>
                </div>
             
        </div>
        
    )
    
}

export default EditExpenseForm
