import React, { useState } from 'react';
import axios from 'axios'
import './App.css'

import Select from 'react-select'


const DeleteExpenseForm = ({show, handleClose, confirm}) => {

    const class_placerholder = show ? 'modal display-block' : 'modal display-none';

    handleConfirm(() => {
        // Update the document title using the browser API
        confirm = true;
    });

    return (
          
        <div className={class_placerholder}>
                <div className='modal-main'>
                    <h2 className="black"> Expense: </h2>
                    <div>
                        
                        <h1>Confirm Deletion</h1>
                        <h2>Are you sure you want to delete this expense? </h2>

                        <div>
                            <button onClick={handleClose}>Cancel</button>
                            <button> onClick={() => {handleConfirm();handleClose();}}Yes</button>
                        </div>
                        
                    </div>
                </div>
             
        </div>
        
    )
    
}

export default DeleteExpenseForm
